<?phpob_start();error_reporting(E_ERROR | E_WARNING | E_PARSE | E_NOTICE);/*  Template Name: Download */?><?php    $id = $_GET['f_id'];    $targ_w = $_GET['f_w'];    $targ_h = $_GET['f_h'];    $jpeg_quality = 90;        increment_download($id); // Summary download        if(isset($_GET['f_ajax'])) {        exit;    }        $post_thumbnail_id = get_post_thumbnail_id($_GET['f_id']);     $upload_dir = wp_upload_dir();    $file = get_post_meta( $post_thumbnail_id, '_wp_attached_file', true);    $imageInfo = wp_get_attachment_image_src($post_thumbnail_id, 'full', true);    $src = $upload_dir['basedir'].'/'.$file;        $img_r = imagecreatefromjpeg($src);        $dst_r = ImageCreateTrueColor($targ_w, $targ_h);    imagecopyresampled($dst_r, $img_r, 0, 0, $_GET['f_x'], $_GET['f_y'], $targ_w, $targ_h, $_GET['f_w'], $_GET['f_h']);    header('Content-type: image/jpeg');    imagejpeg($dst_r, null, $jpeg_quality);    exit;?>